properties.script@STRING:
return {
	name: "Description",
	items: [{
		member: "name",
		labelText: "Name"
	}, {
		member: "model",
					labelText: "Model"
	}, {
		member: "family",
		labelText: "Family",
		typeahead: true
	}, {
		member: "quantityUnit",
		labelText: "Quantity Unit",
		// mandatory: true,
		inputConfig: {
			labelFn: function(entry) {
				return entry.getDescription();
			}
		}
	}, {
		member: "manufacturer",
		labelText: "Manufacturer",
		mandatory: true,
		inputConfig: {
			labelFn: function(entry) {
				return entry.getName();
			}
		}
	}]
};
