properties.script@STRING:
return [
	{
		items: [{
				member: "internalName",
				labelText: "Internal name",
				mandatory: true,
				generated: true,
				init: function(input, entry) {
					entry.addListener("changed", function(entity, member, value, oldValue){
						if(member === "name.translationKey") {
							entry.setInternalName(value);
						}
					}, this);
					entry.addListener("persisted", function(persisted){
						// delegate the persistence event
						entry.getName().fireEvent("persisted", persisted.getName());
					}, this);
				}
			}, {
				member: "name",
				labelText: "Translation",
				config: {
					sections: TranslatedObjectRestBean
				},
				embedded: true
			}]
	}
];
